/** build additional tab panel **/cf.userThirdTab = function(){return {	thePanel				:false,	theAddressFieldset		:false,	thePhoneFaxFieldset		:false,	theCompanyFieldset		:false,	theCommentFieldset		:false,	/** call all necesary functions **/	init:function () {		this.initThirdPanel();		this.initAddressFieldset();		this.initPhoneFaxFieldset();		this.initCompanyFieldset();		this.initCommentFieldset();		this.thePanel.add(this.theAddressFieldset);		this.thePanel.add(this.thePhoneFaxFieldset);		this.thePanel.add(this.theCompanyFieldset);		this.thePanel.add(this.theCommentFieldset);	},		/** init the panel for tab **/	initThirdPanel: function () {		this.thePanel = new Ext.Panel({			modal: true,			closable: false,			modal: true,			width: 650,			height: cf.Layout.theRegionWest.getHeight() + cf.Layout.theRegionNorth.getHeight() - 230,			autoScroll: true,			title: '<?php echo __('Additional data',null,'usermanagement'); ?>',			shadow: false,			minimizable: false,			draggable: false,			resizable: false,	        plain: false		});	},	/** init address fieldset and elements **/	initAddressFieldset: function () {		this.theAddressFieldset = new Ext.form.FieldSet({			title: '<?php echo __('Address data',null,'usermanagement'); ?>',			width: 500,			height: 'auto',			style: 'margin-top:20px;margin-left:5px;',			labelWidth: 180,			items:[{				xtype: 'textfield',				fieldLabel: '<?php echo __('Street',null,'usermanagement'); ?>',				labelWidth: 180,				id: 'userThirdTab_street',				allowBlank: true,				width: 200			},{				xtype: 'panel',				fieldLabel: '<?php echo __('Zip / City',null,'usermanagement'); ?>',				border: false,				layout: 'column',				items:[{					xtype: 'textfield',					style: 'margin-right:5px;',					id: 'userThirdTab_zip',					allowBlank: true,					width: 45				},{					xtype: 'textfield',					id: 'userThirdTab_city',					allowBlank: true,					width: 162				}]			},{				xtype: 'textfield',				fieldLabel: '<?php echo __('Country',null,'usermanagement'); ?>',				labelWidth: 180,				id: 'userThirdTab_country',				allowBlank: true,				width: 200			}]		});				if (Ext.isIE6 == true) {			Ext.getCmp('userThirdTab_zip').style = 'margin-top:0px;margin-bottom:0px;margin-right:5px;';			Ext.getCmp('userThirdTab_city').style = 'margin-top:0px;margin-bottom:0px;';			Ext.getCmp('userThirdTab_city').setSize({width: 164});		}		else if (Ext.isIE7 == true) {			Ext.getCmp('userThirdTab_zip').style = 'margin-top:0px;margin-bottom:0px;margin-right:5px;';			Ext.getCmp('userThirdTab_city').style = 'margin-top:0px;margin-bottom:0px;';			Ext.getCmp('userThirdTab_city').setSize({width: 162});					}	},		/** init phone etc fieldset and elements **/	initPhoneFaxFieldset: function () {		this.thePhoneFaxFieldset = new Ext.form.FieldSet({			title: '<?php echo __('Address data',null,'usermanagement'); ?>',			width: 500,			height: 'auto',			style: 'margin-top:20px;margin-left:5px;',			labelWidth: 180,			items:[{				xtype: 'textfield',				fieldLabel: '<?php echo __('Phone 1',null,'usermanagement'); ?>',				labelWidth: 180,				id: 'userThirdTab_phone1',				allowBlank: true,				width: 200				},{				xtype: 'textfield',				fieldLabel: '<?php echo __('Phone 2',null,'usermanagement'); ?>',				labelWidth: 180,				id: 'userThirdTab_phone2',				allowBlank: true,				width: 200			},{				xtype: 'textfield',				fieldLabel: '<?php echo __('Mobile',null,'usermanagement'); ?>',				labelWidth: 180,				id: 'userThirdTab_mobil',				allowBlank: true,				width: 200			},{				xtype: 'textfield',				fieldLabel: '<?php echo __('Fax',null,'usermanagement'); ?>',				labelWidth: 180,				id: 'userThirdTab_fax',				allowBlank: true,				width: 200			}]		});		},		/** init company fieldset and elements **/	initCompanyFieldset: function () {		this.theCompanyFieldset = new Ext.form.FieldSet({			title: '<?php echo __('Organisation, Department and Burdence center',null,'usermanagement'); ?>',			width: 500,			height: 'auto',			style: 'margin-top:20px;margin-left:5px;',			labelWidth: 180,			items:[{				xtype: 'textfield',				fieldLabel: '<?php echo __('Organisation',null,'usermanagement'); ?>',				labelWidth: 180,				id: 'userThirdTab_organisation',				allowBlank: true,				width: 200			},{				xtype: 'textfield',				fieldLabel: '<?php echo __('Department',null,'usermanagement'); ?>',				labelWidth: 180,				id: 'userThirdTab_department',				allowBlank: true,				width: 200			},{				xtype: 'textfield',				fieldLabel: '<?php echo __('Burdence center',null,'usermanagement'); ?>',				labelWidth: 180,				id: 'userThirdTab_burdencenter',				allowBlank: true,				width: 200			}]		});	},		/** init comment fieldset **/	initCommentFieldset:function () {		this.theCommentFieldset = new Ext.form.FieldSet({			title: '<?php echo __('Comment',null,'usermanagement'); ?>',			width: 500,			height: 'auto',			style: 'margin-top:20px;margin-left:5px;',			labelWidth: 180,			items: [{				xtype: 'textarea',				fieldLabel: '<?php echo __('Comment',null,'usermanagement'); ?>',				id:'userThirdTab_comment',				allowBlank: true,				width: 200,				height: 70			}]		});		}};}();